mapper.select:
select * from orders 
            and number like concat('%',#{number},'%') 
                and phone like concat('%',#{phone},'%') 
                and user_id = #{userId} 
                and
       status = #{status} 
                and order_time >= #{beginTime} 
                and order_time <= #{endTime} 
        order
       by order_time desc

select od.name name, sum(od.number) number from order_detail od
       join orders o on od.order_id = o.id and o.status = 5 and date(o.order_time) between #{begin} and #{end} group by od.name order by
       sum(od.number) desc limit 10;
